package test;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Ruchi is from Aligarh";
		String name1 = "Sharma";
		System.out.println("Number of characters in string is: " +name.length());
		System.out.println("The 6th character in the string is: "+ name.charAt(5));
		System.out.println("The last character in the string is: " + name.charAt(name.length()- 1));
		System.out.println("The string in upper case is " +name1.toUpperCase());
		

	}

}
